import civs from '/civs.json';

export default (req, res) => {
	res.setHeader('Access-Control-Allow-Origin', '*');
	if (req.query.civ) {
		res.status(200).json(civs.filter((civ) => civ.name === req.query.civ)[0]);
	} else {
		res.status(200).json(civs.map((civ) => ({ name: civ.name, icon: civ.icon })));
	}
};
